export default {
  data: () => ({
    dialog: false,
    resolve: null,
    reject: null,
    title: null,
    message: null,
    agreeMessage: null,
    cancelMessage: null,
  }),
  methods: {
    open(title, message, agreeMessage, cancelMessage) {
      this.dialog = true
      this.title = title
      this.message = message
      this.agreeMessage = agreeMessage
      this.cancelMessage = cancelMessage
      return new Promise((resolve, reject) => {
        this.resolve = resolve
        this.reject = reject
      })
    },
    agree() {
      this.resolve(true)
      this.dialog = false
    },
    cancel() {
      this.resolve(false)
      this.dialog = false
    }
  }
}
